import React, { useState, useContext } from "react";
import { AppContext } from "../../context/AppContext"; 
import { createExpense } from "../../utils/expense-utils";

const AddExpenseForm = () => {
  // Exercise: Consume the AppContext here
  const { setExpenses } = useContext(AppContext); 

  // Exercise: Create name and cost to state variables
  const [name, setName] = useState<string>("");
  const [cost, setCost] = useState<number>(0);

  const onSubmit = async(event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();

    if (!name || cost <= 0) {
      alert("Please enter a valid name and cost.");
      return;
    }

    // Exercise: Add add new expense to expenses context array
    const newExpense = {
      id: Date.now().toString(), 
      description: name,
      cost,
    };

    try {
      // Call the backend to create the new expense
      const savedExpense = await createExpense(newExpense);

      // Update the context only after the backend call is successful
      setExpenses((prevExpenses) => [...prevExpenses, savedExpense]);

      // Clear the form inputs
      setName("");
      setCost(0);
    } catch (error) {
      console.error("Failed to add expense:", error);
      alert("There was an issue adding the expense.");
    }

  };

  return (
    <form onSubmit={(event) => onSubmit(event)}>
      <div className="row">
        <div className="col-sm">
          <label htmlFor="name">Name</label>
          <input
            required
            type="text"
            className="form-control"
            id="name"
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            // HINT: onChange={}
          ></input>
        </div>
        <div className="col-sm">
          <label htmlFor="cost">Cost</label>
          <input
            required
            type="number"
            className="form-control"
            id="cost"
            value={cost} 
            onChange={(e) => setCost(parseFloat(e.target.value))} // check
            // HINT: onChange={}
          ></input>
        </div>
        <div className="col-sm">
          <button type="submit" className="btn btn-primary mt-3">
            Save
          </button>
        </div>
      </div>
    </form>
  );
};

export default AddExpenseForm;
