import ExpenseItem from "./ExpenseItem";
import { AppContext, AppContextType } from "../../context/AppContext";
import { useContext, useEffect } from "react";
import { Expense } from "../../types/types";
import { fetchExpenses } from "../../utils/expense-utils";

const ExpenseList = () => {
  const context = useContext(AppContext) as AppContextType;
  const { expenses, setExpenses } = context;
  useEffect(() => {
    loadExpenses();
    }, []);

    // Function to load expenses and handle errors
    const loadExpenses = async () => {
      try {
          const expenseList = await fetchExpenses();
          setExpenses((prevExpenses) => {
              // Filter out duplicates
              const newExpenses = expenseList.filter(
                  (expense) => !prevExpenses.some((e) => e.id === expense.id)
              );
              return [...prevExpenses, ...newExpenses];
          });
      } catch (err: any) {
          console.log(err.message);
      }
  };


  return (
    <ul className="list-group">
      {expenses.map((expense: Expense) => (
        <ExpenseItem key={expense.id} id={expense.id} description={expense.description} cost={expense.cost} />
      ))}
    </ul>
  );
};

export default ExpenseList;
